import React, { useState, useEffect, useRef } from "react";

function App() {
    const [boardImage, setBoardImage] = useState("/images/board.png");
    const videoRef = useRef(null);

    // Atualiza o tabuleiro via API REST
    useEffect(() => {
        const updateBoard = () => {
            const timestamp = new Date().getTime();
            setBoardImage(`http://localhost:5000/board?timestamp=${timestamp}`);
        };

        const interval = setInterval(updateBoard, 1000);

        return () => clearInterval(interval);
    }, []);

    // Captura a câmera do usuário
    useEffect(() => {
        navigator.mediaDevices.getUserMedia({ video: true })
            .then((stream) => {
                if (videoRef.current) {
                    videoRef.current.srcObject = stream;
                }
            })
            .catch((err) => {
                console.error("Erro ao acessar a câmera: ", err);
            });

        return () => {
            if (videoRef.current && videoRef.current.srcObject) {
                videoRef.current.srcObject.getTracks().forEach(track => track.stop());
            }
        };
    }, []);

    return (
        <div className="app">
            <img src="/images/Navbar.png" alt="Navbar" className="navbar-image" />
            <div className="split left">
                <img src="/images/image 1.svg" alt="Icon" className="image-left" />
                <div className="container-left">
                    <video ref={videoRef} autoPlay playsInline className="camera-feed" />
                </div>
                <img src="/images/Cute Avatar.svg" alt="Cute Avatar" className="image-1" />
                <img src="/images/x.svg" alt="X Icon" className="image-2" />
                <img src="/images/Preview.svg" alt="Preview Icon" className="image-3" />
            </div>
            <div className="split right">
                <div className="div-board">
                    <img src={boardImage} alt="Board" className="board" />
                </div>
                <img src="/images/image 2.png" alt="Piece" className="piece-right-image" />
            </div>
        </div>
    );
}

export default App;
